import React, { useState, useEffect } from 'react';
import { Link, useNavigatem, useParams } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';
import { ToastContainer, toast } from 'react-toastify';

import { getSpecificSurvey } from '../utils/APIRoutes';

export default function TakeSurvey() {
  const { uid, id } = useParams();
  const [survey, setsurvey] = useState({});
  useEffect(() => {
    if (id && uid) {
      async function getSurvey() {
        const { data } = await axios.post(`${getSpecificSurvey}/${id}/view`, {
          userId: uid,
        });
        if (data.status) console.log(data);
        setsurvey(data.survey);
        //   :id/view
      }
      getSurvey();
    }
  }, [id, uid]);
  survey.status && console.log(survey.questions);
  return (
    <Component>
      <form action="">
        <h3>{survey.surveyName}</h3>
        {surveysurvey.questions.map((question) => {
          return <div>{document.write(question.question)}</div>;
        })}
      </form>
    </Component>
  );
}

const Component = styled.div`
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
`;
