import React from 'react';

import styled from 'styled-components';

import { v4 as uuidv4 } from 'uuid';

export default function CreateQuestion() {
  return (
    <Component>
      <div id={uuidv4()} className="question">
        <div className="question-div">
          <input type="text" className="question-input" placeholder=" " />
          <label>question</label>
        </div>
        <div className="answer-type-div">
          <label>Answer Type</label>
          <div className="wrap-ans-type">
            <input id="radio-btn" name="ans-type" type="radio" />
            <label htmlFor="radio-btn">Radio Button</label>
            <input id="check-box" name="ans-type" type="radio" />
            <label htmlFor="check-box">Check Box</label>
            <input id="drop-down" name="ans-type" type="radio" />
            <label htmlFor="drop-down">Drop down</label>
            <input id="text" name="ans-type" type="radio" />
            <label htmlFor="text">Text</label>
          </div>
        </div>
      </div>
    </Component>
  );
}

const Component = styled.div`
  .question {
    box-shadow: 8px 8px 18px hsl(0 0% 0% / 0.14),
      -8px -8px 18px hsl(0 100% 100%);
    padding: 12px 7px;
    .question-div {
      background: red;
      position: relative;
      border-radius: 5px;
      margin-bottom: 1.5rem;
      height: 54px;
      .question-input {
        height: 100%;
        width: 100%;
        border: none;
        outline: none;
        border-radius: 5px;
        padding-top: 13px;
        padding-left: 8px;
        font: 400 1rem var(--openSansFont);
        &:focus + label,
        &:not(:placeholder-shown) + label {
          font-size: 0.87rem;
          top: 4px;
          color: hsl(0 0% 0% / 0.48);
        }
      }
      label {
        position: absolute;
        left: 8px;
        top: 16px;
        font: 400 1rem var(--openSansFont);
        transition: all 0.3s ease-in-out;
      }
    }
    .answer-type-div {
      background: linear-gradient(145deg, #cacaca, #f0f0f0);
      box-shadow: 10px 10px 20px hsl(0 0% 0% /0.27), -10px -10px 20px #ffffff;
      display: flex;
      
    }
  }
`;
