import React, { useState, useEffect } from 'react';
import { Link, useNavigatem, useParams } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';
import { ToastContainer, toast } from 'react-toastify';

import { getSpecificSurvey } from '../utils/APIRoutes';

export default function TakeSurvey() {
  const { uid, id } = useParams();
  const [questions, setQuestions] = useState([]);
  const [survey, setsurvey] = useState();
  console.log(typeof questions);
  useEffect(() => {
    if (id && uid) {
      async function getSurvey() {
        const { data } = await axios.post(`${getSpecificSurvey}/${id}/view`, {
          userId: uid,
        });
        setsurvey(data.survey);
      }
      getSurvey();
    }
  }, [id, uid]);
  return (
    <Component>
      <form action="">
        <h3>{survey?.surveyName}</h3>
        {questions.length > 0 &&
          questions?.map((q) => {
            return (
              <div key={q.id}>
                <div className="question">{q.question}</div>
              </div>
            );
          })}
      </form>
    </Component>
  );
}

const Component = styled.div`
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 2% 1%;
  form {
    width: 380px;
    height: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 1%;
    background: red;
    h3 {
      font: 500 1.2rem var(--ralewayFont);
      margin-bottom: 1rem;
    }
    .question {
      font: 400 1.1rem var(--poppinsFont);
      align-self: flex-start;
      margin-right: auto;
      background: lime;
      margin-bottom: 0.8rem;
    }
  }
`;
