import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Form from '../components/Forms';
import styled from 'styled-components';

export default function AllForms({ forms, setForms }) {
  console.log('a', forms);
  return (
    <Container>
      {forms.map((form) => {
        // return <Form form={form} key={form._id} />;
        return (
          <div key={form._id} className="form">
            <h2>{form.surveyName} fasdasdfasdfsdasdf</h2>
            <div className="hr"></div>

            <div className="wrapper">
              <label>Number of questions</label>
              <div className="value">{form.questions.length}</div>
            </div>

            <div className="wrapper">
              <label>Amount allocated</label>
              <div className="value">{form.amount}</div>
            </div>

            <div className="wrapper">
              <label>date created</label>
              <div className="value">{form.createdDate}</div>
            </div>
            {/* <button className="view-btn"> */}
            <Link to={`/admin/home/${form._id}`}>View Form</Link>
          </div>
        );
      })}
    </Container>
  );
}

const Container = styled.div`
  height: 100%;
  display: grid;
  grid-template-columns: repeat(auto-fit, 240px);
  gap: 1.5rem;

  @media screen and (max-width: 608px) {
    justify-content: center;
  }
`;
