/* eslint-disable react-hooks/exhaustive-deps */
import React, { useState, useRef, useEffect } from 'react';

import styled from 'styled-components';
import { v4 as uuidv4 } from 'uuid';
import { ToastContainer, toast } from 'react-toastify';

import Option from './Option';

export default function CreateQuestion({ questions, setQuestions, data }) {
  const [tempQuestion, setTempQuestion] = useState({
    questionId: data.questionId,
    question: data.question,
    answerType: data.answerType,
    required: data.required,
    option: data.option,
  });
  console.log(data.questionId);
  async function handleInputChange(e) {
    const { name, value, customid } = e.target;
    console.log('cid ', customid);
    setTempQuestion((preVal) => ({
      ...preVal,
      [name]: value,
    }));
    if (questions.length > 0) {
      const tempSpread = [...questions];
      tempSpread.forEach((q) => {
        // console.log(q.questionId === tempQuestion.questionId);
        // console.log(q.questionId, tempQuestion.questionId);
        console.log(name);
        console.log(q.questionId === customid);
        console.log(q.questionId, customid);
        if (q.questionId === customid) {
          console.log(name === 'question');
          'question' === name && (q.question = value);
          'answerType' === name && (q.answerType = value);
          'required' === name && (q.required = value);
        }
      });
      setQuestions([...tempSpread]);
    } else {
      setQuestions([...questions, { ...tempQuestion, [name]: value }]);
    }
  }

  function setOptions(value, index) {
    console.log(value);
    if (index === 'new') index = tempQuestion.option.length;
    if (tempQuestion.option.includes(value)) {
      !toast.isActive('sur-tid') &&
        toast.warning('duplicate option not allowed', {
          toastId: 'sur-tid',
          position: 'top-center',
          autoClose: 7000,
          pauseOnHover: true,
          draggable: true,
          theme: 'colored',
        });
      return;
    } else {
      const tempVal = { ...tempQuestion };
      const optArr = [...tempVal.option];
      optArr[index] = value;

      setTempQuestion((preVal) => ({
        ...preVal,
        option: [...optArr],
      }));
      if (questions.length > 0) {
        const tempSpread = [...questions];
        tempSpread.forEach((q) => {
          console.log(q.questionId);
          q.questionId === tempQuestion.questionId && (q.option[index] = value);
        });
        setQuestions([...tempSpread]);
      }
    }
  }

  function removeOption(index) {
    if (tempQuestion.option.length === 1) return;
    const tempVal = { ...tempQuestion };
    const tempArr = [...tempVal.option];
    tempArr.splice(index, 1);
    setTempQuestion((preVal) => ({
      ...preVal,
      option: [...tempArr],
    }));
    if (questions.length > 0) {
      const tempSpread = [...questions];
      tempSpread.forEach((q) => {
        q.questionId === tempQuestion.questionId && q.option.splice(index);
      });
      setQuestions([...tempSpread]);
    }
  }

  return (
    <Component>
      <ToastContainer />

      <div id={data.questionId} className="question">
        <div className="question-div">
          <input
            type="text"
            name="question"
            className="question-input"
            onChange={handleInputChange}
            placeholder=" "
            customid={data.questionId}
            value={tempQuestion.question}
          />
          <label>question</label>
        </div>
        <div className="answer-type-div">
          <label>Answer Type</label>
          <div className="ans-type">
            <div className="wrap-ans-type">
              <input
                id="radio-btn"
                value="radio-btn"
                name="answerType"
                onClick={handleInputChange}
                customid={data.questionId}
                type="radio"
              />
              <label htmlFor="radio-btn">Radio Button</label>
            </div>
            <div className="wrap-ans-type">
              <input
                id="check-box"
                value="check-box"
                name="answerType"
                onClick={handleInputChange}
                customid={data.questionId}
                type="radio"
              />
              <label htmlFor="check-box">Check Box</label>
            </div>
            <div className="wrap-ans-type">
              <input
                id="drop-down"
                value="drop-down"
                name="answerType"
                onClick={handleInputChange}
                type="radio"
              />
              <label htmlFor="drop-down">Drop down</label>
            </div>
            <div className="wrap-ans-type">
              <input
                id="text"
                value="text"
                name="answerType"
                onClick={handleInputChange}
                type="radio"
              />
              <label htmlFor="text">Text</label>
            </div>
          </div>
        </div>
        <div className="required-div">
          <label>Required</label>
          <div className="wrap-required">
            <input
              id="yes"
              type="radio"
              name="required"
              value="true"
              onClick={handleInputChange}
            />
            <label htmlFor="yes">Yes</label>
          </div>
          <div className="wrap-required">
            <input
              id="no"
              type="radio"
              name="required"
              value="false"
              onClick={handleInputChange}
            />
            <label htmlFor="no">No</label>
          </div>
        </div>
        {['radio-btn', 'drop-down', 'check-box'].includes(
          tempQuestion.answerType
        ) &&
          tempQuestion.option.map((opt, index) => (
            <div key={index} className="wrap-option">
              <Option value={opt} index={index} setOptions={setOptions} />

              <button
                className="option-button close-btn"
                onClick={() => removeOption(index)}
              >
                <i className="fa-solid fa-xmark"></i>Remove
              </button>
            </div>
          ))}
        {['radio-btn', 'drop-down', 'check-box'].includes(
          tempQuestion.answerType
        ) && (
          <button
            className="option-button add-btn"
            onClick={() => setOptions('option', 'new')}
          >
            <i className="fa-solid fa-plus"></i> Add Option
          </button>
        )}
      </div>
    </Component>
  );
}

const Component = styled.div`
  .question {
    box-shadow: 8px 8px 18px hsl(0 0% 0% / 0.14),
      -8px -8px 18px hsl(0 100% 100%);
    padding: 12px 7px;
    margin-bottom: 2rem;
    .question-div {
      background: red;
      position: relative;
      border-radius: 5px;
      margin-bottom: 1.5rem;
      height: 54px;
      .question-input {
        height: 100%;
        width: 100%;
        border: none;
        outline: none;
        border-radius: 5px;
        padding-top: 13px;
        padding-left: 8px;
        font: 400 1rem var(--openSansFont);
        &:focus + label,
        &:not(:placeholder-shown) + label {
          font-size: 0.87rem;
          top: 4px;
          color: hsl(0 0% 0% / 0.48);
        }
      }
      label {
        position: absolute;
        left: 8px;
        top: 16px;
        font: 400 1rem var(--openSansFont);
        transition: all 0.3s ease-in-out;
      }
    }
    .answer-type-div {
      /* background: linear-gradient(145deg, #dfdfdf, #f0f0f0); */
      /* box-shadow: 10px 10px 20px hsl(0 0% 0% /0.27), -10px -10px 20px #ffffff; */
      display: flex;
      flex-direction: column;
      gap: 8px;
      padding: 8px;
      border-radius: 5px;
      margin-bottom: 0.7rem;
      & > label {
        font: 400 1rem var(--openSansFont);
        color: hsl(0 0% 0% / 0.58);
      }
      .ans-type {
        display: grid;
        gap: 0.5rem;
        grid-template-columns: repeat(auto-fit, minmax(100px, 130px));
        .wrap-ans-type {
          display: flex;
          gap: 0.4rem;
          align-items: center;
          input {
            accent-color: hsl(265 100% 49%);
          }
          input:checked + label {
            color: hsl(265 100% 49%);
          }
          label {
            font: 400 1rem var(--openSansFont);
          }
        }
      }
    }
    .required-div {
      padding: 8px;
      display: flex;
      gap: 0.8rem;
      margin-bottom: 1rem;
      label {
        font: 400 1rem var(--openSansFont);
      }
      & > label {
        color: hsl(0 0% 0% / 0.58);
      }
      .wrap-required {
        display: flex;
        gap: 0.4rem;
        input {
          accent-color: hsl(265 100% 49%);
        }
        input:checked + label {
          color: hsl(265 100% 49%);
        }
      }
    }
    .wrap-option {
      display: flex;
