import React from 'react';
import styled from 'styled-components';

export default function AlertModal({ functionHandler }) {
  return (
    <Component>
      <div className="alertModal">
        <div className="head">
          <i class="fa-solid fa-triangle-exclamation"></i>
          <h2>Warning</h2>
        </div>
        <div className="content">Are you sure you want to delete</div>
        <div className="wrap-btn">
          <button>Cancel</button>
          <button>Delete</button>
        </div>
      </div>
    </Component>
  );
}

const Component = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  display: flex;
  justify-content: center;
  height: 100%;
  width: 100%;
  background: hsl(0 0% 0% / 0.57);
  backdrop-filter: blur(3px);
  user-select: none;
  .alertModal {
    margin-top: 3rem;
    background: hsl(0 100% 100% / 0.52);
    height: fit-content;
    padding: 12px;
    border-radius: 5px;
    backdrop-filter: blur(4px);

    .head {
      display: flex;
      gap: 8px;
      align-items: center;
      .fa-triangle-exclamation {
        font-size: 1.5rem;
        color: hsl(50 100% 40%);
      }
    }
  }
`;
