/* eslint-disable react-hooks/exhaustive-deps */
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';
import { ToastContainer, toast } from 'react-toastify';

export default function UserHome() {
  const navigate = useNavigate();
  const [user, setUser] = useState({ name: '', survey: [], amount: 0 });
  useEffect(() => {
    async function fetchUser() {
        if (!localStorage.getItem('sur-uid')) navigate('/user/login');
        else {
            let temp = await JSON.parse(localStorage.getItem('sur-uid'));
            setUser({name: temp.userName,survey:temp.survey,amount:temp.amount});
            
      }
    }
    fetchUser();
  }, []);
  return (
    <Component>
      <ToastContainer />

      <header>
        <div className="greeting">
          <h3>
            Welcome <span>{user.name}</span> ðŸ‘‹
          </h3>
        </div>
        <div className="wallet">amount: ${user.amount}</div>
      </header>
    </Component>
  );
}
const Component = styled.div``;
