/* eslint-disable react-hooks/exhaustive-deps */
import React, { useState, useEffect } from 'react';
import { ToastContainer, toast } from 'react-toastify';

import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';
import AllForms from './AllForms';

import { fetchAllForms } from '../utils/APIRoutes';

export default function AdminHome() {
  const navigate = useNavigate();
  const [admin, setAdmin] = useState({ id: '', name: '' });
  const [forms, setForms] = useState({ status: false, data: [] });

  useEffect(() => {
    async function fetchUser() {
      if (!localStorage.getItem('sur-uid')) navigate('/admin/login');
      else {
        let adminObj = await JSON.parse(localStorage.getItem('sur-uid'));
        await setAdmin({ id: adminObj._id, name: adminObj.name });
        adminObj = undefined;
      }
    }
    fetchUser();
  }, []);

  useEffect(() => {
    async function fetchForms() {
    }
    fetchForms();
  }, [admin]);

  return (
    <Container>
      <ToastContainer />

      <header>
        <div className="greeting">
          <h3>
            Welcome <span>rishikesh</span> ðŸ‘‹
          </h3>
        </div>
        <button className="create-btn">create form</button>
      </header>
      {forms.status && <AllForms forms={forms.data} setForms={setForms} />}
    </Container>
  );
}

const Container = styled.div`
  height: 100vh;
  padding: 3% 4% 1%;
  background: #dfdfdf;
  display: flex;
  flex-direction: column;
  header {
    display: flex;
    align-items: center;
    margin-bottom: 1rem;
    /* border-radius: 3px;
    background: hsl(270 88% 48% / 0.15);
    box-shadow: 0 8px 12px hsl(0 0% 0% / 0.09);
    backdrop-filter: blur(28px) saturate(100%); */

    .greeting {
      h3 {
        font: 500 1.3rem var(--openSansFont);
      }
    }
    .create-btn {
      margin-left: auto;
      border: none;
      outline: none;
      color: #ececec;
      background: hsl(190 88% 48%);
      border-radius: 4px;
      padding: 0.4rem 0.8rem;
      font: 600 1.4rem var(--openSansFont);
      cursor: pointer;
    }
  }
  @media screen and (max-width: 768px) {
    header {
      .greeting {
        h3 {
          font-size: 1.13rem;
        }
      }
      .create-btn {
        font-size: 0.95rem;
      }
    }
  }
`;
