/* eslint-disable react-hooks/exhaustive-deps */
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';
import { ToastContainer, toast } from 'react-toastify';

import { getAllSurvey } from '../utils/APIRoutes';

export default function UserHome() {
  const navigate = useNavigate();
  const [user, setUser] = useState({ name: '', survey: [], amount: 0 });
  const [survey, setSurvey] = useState([]);

  useEffect(() => {
    async function fetchUser() {
      if (!localStorage.getItem('sur-uid')) navigate('/user/login');
      else {
        let temp = await JSON.parse(localStorage.getItem('sur-uid'));
        setUser({
          name: temp.name,
          survey: temp.survey,
          amount: temp.amount,
        });
        temp = undefined;
      }
    }
    fetchUser();
  }, []);

  useEffect(() => {
    async function fetchForms() {
      if (user) {
        const { data } = await axios.post(getAllSurvey);
        if (data.status === false)
          !toast.isActive('sur-tid') &&
            toast.error(data.msg, {
              toastId: 'sur-tid',
              position: 'top-right',
              autoClose: 7000,
              pauseOnHover: true,
              draggable: true,
              theme: 'dark',
            });
        console.log(data.response);
        if (data.status === true)
          setSurvey({ status: data.status, data: data.response });
      }
    }
    fetchForms();
  }, []);
  return (
    <Component>
      <ToastContainer />

      <header>
        <div className="greeting">
          <h3>
            Welcome <span>{user.name}</span> ðŸ‘‹
          </h3>
        </div>
        <div className="wallet">amount: {user.amount}</div>
          </header>
          {
              survey.status && survey.data.map
          }
    </Component>
  );
}
const Component = styled.div`
  height: 100vh;
  padding: 3% 4% 1%;
  background: #dfdfdf;
  display: flex;
  flex-direction: column;
  header {
    display: flex;
    align-items: center;
    margin-bottom: 1rem;
    /* border-radius: 3px;
    background: hsl(270 88% 48% / 0.15);
    box-shadow: 0 8px 12px hsl(0 0% 0% / 0.09);
    backdrop-filter: blur(28px) saturate(100%); */

    .greeting {
      h3 {
        font: 500 1.3rem var(--openSansFont);
      }
    }
    .wallet {
      margin-left: auto;
      font: 500 1.3rem var(--openSansFont);
    }
  }
`;
