import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigatem, useParams } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';
import { ToastContainer, toast } from 'react-toastify';

import { getSpecificSurvey } from '../utils/APIRoutes';
import { v4 as uuidv4 } from 'uuid';

export default function TakeSurvey() {
  const { uid, id, cid } = useParams();
  const answerRef = useRef([]);
  const [survey, setsurvey] = useState({
    surveyName: '',
    questions: [],
    amount: 0,
  });
  useEffect(() => {
    if (id && uid) {
      async function getSurvey() {
        const { data } = await axios.post(`${getSpecificSurvey}/${id}/view`, {
          userId: uid,
        });
        setsurvey({
          surveyName: data.survey.surveyName,
          questions: data.survey.questions,
          amount: data.survey.amount,
        });
      }
      getSurvey();
    }
  }, [id, uid]);

  function sendSurvey(e) {
    e.preventDefault();
    const finalAnswers = {
      surveyId: id,
      userId: uid,
      creatorId: cid,
      surveyName: survey.surveyName,
    };
    const answers = [];
    survey.questions.forEach((quest) => {
      console.log(quest);
      const option = [];
      answerRef.current.forEach((opt) => {
        if (quest.id === opt.id) {
          if (quest.answerType === 'radio-btn') {
            console.log(
              document.querySelector(`[name="${quest.id}"]:checked`).value
            );
            if (quest.answerType === 'drop-down') {
              console.log(document.querySelector(`[name="${quest.id}"]`).value);
            }
          }
        }
      });
      const obj = {
        id: quest.id,
        question: quest.question,
        answerType: quest.answerType,
        required: quest.required,
      };
    });
  }
  return (
    <Component>
      <form onSubmit={sendSurvey}>
        <h3>{survey.surveyName}</h3>
        {survey.questions &&
          survey.questions.map((q) => {
            return (
              <div
                ref={(ref) =>
                  answerRef.current.push({ id: q.id, value: 'set' })
                }
                className="set"
                key={uuidv4()}
              >
                <div className="question">{q.question}</div>
                {q.answerType === 'radio-btn' &&
                  q?.option.map((opt, index) => {
                    return (
                      <div key={uuidv4()} className="radio">
                        <input
                          name={q?.id}
                          id={q.id}
                          type="radio"
                          value={opt}
                        />
                        <label htmlFor={q.id + index}>{opt}</label>
                      </div>
                    );
                  })}
                {q.answerType === 'drop-down' && (
                  <select name={q.id} id={q.id}>
                    {q.option.map((opt, index) => {
                      return <option key={uuidv4()}>{opt}</option>;
                    })}
                  </select>
                )}
              </div>
            );
          })}
        <button className="submit">Submit</button>
      </form>
    </Component>
  );
}

const Component = styled.div`
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 2% 1%;
  background: #ffffff99;
  form {
    width: 380px;
    height: 100%;
    display: flex;
    flex-direction: column;
    padding: 1% 0.8rem;
    background: hsl(0 100% 100% / 0.35);
    background: linear-gradient(145deg, hsl(270 100% 39%), hsl(280 100% 35%));
    backdrop-filter: blur(29px) saturate(100%);
    box-shadow: 0 0 30px hsl(0 0% 0% / 0.21);
    h3 {
      font: 500 1.2rem var(--ralewayFont);
      margin-bottom: 1rem;
      align-self: center;
      color: #ffffff;
    }
    .set {
      margin-bottom: 1.4rem;
    }
    .question {
      font: 400 1.1rem var(--poppinsFont);
      margin-bottom: 0.8rem;
      color: #ffffffda;
    }
    .radio {
      display: flex;
      /* flex-direction: column; */
      margin-bottom: 0.3rem;
      gap: 0.3rem;
      input {
        border: none;
        font-size: 0.9rem;
        background: none;
        accent-color: #282828;
      }
      label {
        font: 400 0.91rem var(--poppinsFont);
        color: #ffffff99;
      }
    }
    select {
      border: none;
      outline: none;
      cursor: pointer;
      padding: 0.4rem 0.7rem;
      box-shadow: 0 9px 28px #0000004a;
      font: 400 0.99rem var(--openSansFont);
      border-radius: 5px;
    }
    .submit {
      align-self: center;
      padding: 0.4rem;
      font: 800 1.4rem var(--openSansFont);
      width: 50%;
      border-radius: 5px;
      border: none;
      outline: none;
      color: hsl(270 100% 59%);
      cursor: pointer;
      box-shadow: 0 9px 28px hsl(0 0% 0%/ 0.29);
    }
  }
`;
