        async function fetchForms() {
          if (admin) {
            const { data } = await axios.post(getAllSurvey, {
              adminId: admin?.id,
            });
            if (data.status === false)
              !toast.isActive('sur-tid') &&
                toast.error(data.msg, {
                  toastId: 'sur-tid',
                  position: 'top-right',
                  autoClose: 7000,
                  pauseOnHover: true,
                  draggable: true,
                  theme: 'dark',
                });
            if (data.status === true)
              setForms({ status: data.status, data: data.response });
          }
        }
        fetchForms();
      }, [admin]);
    
    
  return (
    <Component>
      <ToastContainer />

      <header>
        <div className="greeting">
          <h3>
            Welcome <span>{user.name}</span> ðŸ‘‹
          </h3>
        </div>
        <div className="wallet">amount: {user.amount}</div>
      </header>
    </Component>
  );
}
const Component = styled.div`
  height: 100vh;
  padding: 3% 4% 1%;
  background: #dfdfdf;
  display: flex;
  flex-direction: column;
  header {
    display: flex;
    align-items: center;
    margin-bottom: 1rem;
    /* border-radius: 3px;
    background: hsl(270 88% 48% / 0.15);
    box-shadow: 0 8px 12px hsl(0 0% 0% / 0.09);
    backdrop-filter: blur(28px) saturate(100%); */

    .greeting {
      h3 {
        font: 500 1.3rem var(--openSansFont);
      }
    }
    .wallet {
      margin-left: auto;
      font: 500 1.3rem var(--openSansFont);
    }
  }
`;
