/* eslint-disable react-hooks/exhaustive-deps */
import React, { useState, useRef, useEffect } from 'react';

import styled from 'styled-components';

import { v4 as uuidv4 } from 'uuid';

export default function CreateQuestion({ questions, setQuestions }) {
  const questionIdRef = useRef();
  useEffect(() => {
    questionIdRef.current = uuidv4();
    tempQuestion.questionId = questionIdRef.current;
  }, []);

  const [tempQuestion, setTempQuestion] = useState({
    questionId: '',
    question: '',
    answerType: '',
    required: '',
    option: [],
  });
  function handleQuestionChange(e) {
    const { name, value } = e.target;
    setTempQuestion((preVal) => ({
      ...preVal,
      [name]: value,
    }));
    questions.length > 0
      ? setQuestions(
          questions.filter(
            (q) =>
              q.questionId === tempQuestion.questionId && {
                ...q,
                [q.question]: value,
              }
          )
        )
      : setQuestions({ ...tempQuestion, [name]: value });
  }
  function handleAnswerTypeChange(e) {
    const { name, value } = e.type;
    
  }
  return (
    <Component>
      <div id={questionIdRef.current} className="question">
        <div className="question-div">
          <input
            type="text"
            name="question"
            className="question-input"
            onChange={handleQuestionChange}
            placeholder=" "
            value={tempQuestion.question}
          />
          <label>question</label>
        </div>
        <div className="answer-type-div">
          <label>Answer Type</label>
          <div className="ans-type">
            <div className="wrap-ans-type">
              <input id="radio-btn" name="ans-type" type="radio" />
              <label htmlFor="radio-btn">Radio Button</label>
            </div>
            <div className="wrap-ans-type">
              <input id="check-box" name="ans-type" type="radio" />
              <label htmlFor="check-box">Check Box</label>
            </div>
            <div className="wrap-ans-type">
              <input id="drop-down" name="ans-type" type="radio" />
              <label htmlFor="drop-down">Drop down</label>
            </div>
            <div className="wrap-ans-type">
              <input id="text" name="ans-type" type="radio" />
              <label htmlFor="text">Text</label>
            </div>
          </div>
        </div>
        <div className="required-div">
          <label>Required</label>
          <div className="wrap-required">
            <input id="yes" type="radio" name="required" />
            <label htmlFor="yes">Yes</label>
          </div>
          <div className="wrap-required">
            <input id="no" type="radio" name="required" />
            <label htmlFor="no">No</label>
          </div>
        </div>
      </div>
    </Component>
  );
}

const Component = styled.div`
  .question {
    box-shadow: 8px 8px 18px hsl(0 0% 0% / 0.14),
      -8px -8px 18px hsl(0 100% 100%);
    padding: 12px 7px;
    .question-div {
      background: red;
      position: relative;
      border-radius: 5px;
      margin-bottom: 1.5rem;
      height: 54px;
      .question-input {
        height: 100%;
        width: 100%;
        border: none;
        outline: none;
        border-radius: 5px;
        padding-top: 13px;
        padding-left: 8px;
        font: 400 1rem var(--openSansFont);
        &:focus + label,
        &:not(:placeholder-shown) + label {
          font-size: 0.87rem;
          top: 4px;
          color: hsl(0 0% 0% / 0.48);
        }
      }
      label {
        position: absolute;
        left: 8px;
        top: 16px;
        font: 400 1rem var(--openSansFont);
        transition: all 0.3s ease-in-out;
      }
    }
    .answer-type-div {
      /* background: linear-gradient(145deg, #dfdfdf, #f0f0f0); */
      /* box-shadow: 10px 10px 20px hsl(0 0% 0% /0.27), -10px -10px 20px #ffffff; */
      display: flex;
      flex-direction: column;
      gap: 8px;
      padding: 8px;
      border-radius: 5px;
      margin-bottom: 0.7rem;
      & > label {
        font: 400 1rem var(--openSansFont);
        color: hsl(0 0% 0% / 0.58);
      }
      .ans-type {
        display: grid;
        gap: 0.5rem;
        grid-template-columns: repeat(auto-fit, minmax(100px, 130px));
        .wrap-ans-type {
          display: flex;
          gap: 0.4rem;
          align-items: center;
          input {
            accent-color: hsl(265 100% 49%);
          }
          input:checked + label {
            color: hsl(265 100% 49%);
          }
          label {
            font: 400 1rem var(--openSansFont);
          }
        }
      }
    }
    .required-div {
      padding: 8px;
      display: flex;
      gap: 0.8rem;
      margin-bottom: 1rem;
      label {
        font: 400 1rem var(--openSansFont);
      }
      & > label {
        color: hsl(0 0% 0% / 0.58);
      }
      .wrap-required {
        display: flex;
        gap: 0.4rem;
        input {
          accent-color: hsl(265 100% 49%);
        }
        input:checked + label {
          color: hsl(265 100% 49%);
        }
      }
    }
  }
`;
