import React from 'react';

import styled from 'styled-components';

export default function Option({ value, index }) {
  console.log('called');
  return (
    <Component>
      <div className="option-div">
        <input className="option" type="text" defaultValue={value} />
      </div>
    </Component>
  );
}

const Component = styled.div`
  margin: 0 8px 1rem 8px;
  display: flex;
  flex-direction: column;
  .option {
    border: none;
    outline: none;
    padding: 7px;
    font: 400 1.12rem var(--openSansFont);
    color: #efefef;
    border-radius: 4px;
    background: hsl(265 100% 49% / 0.3);
    background: hsl(0 2% 12% / 0.8);
    border-bottom: 2px solid hsl(265 100% 49%);
  }
`;
