/* eslint-disable react-hooks/exhaustive-deps */
import React, { useState, useRef, useEffect } from 'react';

import styled from 'styled-components';
import { v4 as uuidv4 } from 'uuid';
import { ToastContainer, toast } from 'react-toastify';

import Option from './Option';

export default function CreateQuestion({ questions, setQuestions }) {
  const questionIdRef = useRef();
  useEffect(() => {
    questionIdRef.current = uuidv4();
    tempQuestion.questionId = questionIdRef.current;
  }, []);

  const [tempQuestion, setTempQuestion] = useState({
    questionId: '',
    question: '',
    answerType: '',
    required: '',
    option: ['option'],
  });
  async function handleInputChange(e) {
    const { name, value } = e.target;
    setTempQuestion((preVal) => ({
      ...preVal,
      [name]: value,
    }));
    if (questions.length > 0) {
      const tempSpread = [...questions];
      tempSpread.forEach((q) => {
        console.log(q.questionId === tempQuestion.questionId)
        console.log(name)
        if (q.questionId === tempQuestion.questionId) {
          'question' === name && (q.question = value);
          answerType === name && (q.answerType = value);
          q.required === name && (q.required = value);
        }
      });
      setQuestions([...tempSpread]);
      //   setQuestions(
      //     questions.filter(
      //       (q) =>
      //         q.questionId === tempQuestion.questionId && {
      //           ...q,
      //           [q.question]: value,
      //         }
      //     )
      //   );
    } else {
      setQuestions([...questions, { ...tempQuestion, [name]: value }]);
    }
  }

  function setOptions(value, index) {
    if (index === 'new') index = tempQuestion.option.length;
    if (tempQuestion.option.includes(value)) {
      !toast.isActive('sur-tid') &&
        toast.error('duplicate option not allowed', {
          toastId: 'sur-tid',
          position: 'top-center',
          autoClose: 7000,
          pauseOnHover: true,
          draggable: true,
          theme: 'dark',
        });
      return;
    } else {
      const tempVal = { ...tempQuestion };
      const optArr = [...tempVal.option];
      optArr[index] = value;

      setTempQuestion((preVal) => ({
        ...preVal,
        option: [...optArr],
      }));
      if (questions.length > 0) {
        const tempSpread = [...questions];
        tempSpread.forEach((q) => {
          q.questionId === tempQuestion.questionId && (q.option[index] = value);
        });
        setQuestions([...tempSpread]);
      }
    }
  }

  function removeOption(index) {
    if (tempQuestion.option.length === 1) return;
    const tempVal = { ...tempQuestion };
    const tempArr = [...tempVal.option];
    tempArr.splice(index, 1);
    setTempQuestion((preVal) => ({
      ...preVal,
      option: [...tempArr],
    }));
    if (questions.length > 0) {
      const tempSpread = [...questions];
      tempSpread.forEach((q) => {
        q.questionId === tempQuestion.questionId && q.option.splice(index);
      });
      setQuestions([...tempSpread]);
    }
  }

  return (
    <Component>
      <ToastContainer />

      <div id={questionIdRef.current} className="question">
        <div className="question-div">
          <input
            type="text"
            name="question"
            className="question-input"
            onChange={handleInputChange}
            placeholder=" "
            value={tempQuestion.question}
          />
          <label>question</label>
        </div>
        <div className="answer-type-div">
          <label>Answer Type</label>
          <div className="ans-type">
            <div className="wrap-ans-type">
              <input
                id="radio-btn"
                value="radio-btn"
                name="answerType"
                onClick={handleInputChange}
                type="radio"
              />
              <label htmlFor="radio-btn">Radio Button</label>
            </div>
            <div className="wrap-ans-type">
              <input
                id="check-box"
                value="check-box"
                name="answerType"
                onClick={handleInputChange}
                type="radio"
              />
              <label htmlFor="check-box">Check Box</label>
            </div>
            <div className="wrap-ans-type">
              <input
                id="drop-down"
                value="drop-down"
                name="answerType"
                onClick={handleInputChange}
                type="radio"
              />
              <label htmlFor="drop-down">Drop down</label>
            </div>
            <div className="wrap-ans-type">
              <input
                id="text"
                value="text"
                name="answerType"
                onClick={handleInputChange}
                type="radio"
              />
              <label htmlFor="text">Text</label>
            </div>
          </div>
        </div>
        <div className="required-div">
          <label>Required</label>
          <div className="wrap-required">
            <input
              id="yes"
              type="radio"
              name="required"
              value="true"
              onClick={handleInputChange}
            />
            <label htmlFor="yes">Yes</label>
          </div>
          <div className="wrap-required">
            <input
              id="no"
              type="radio"
              name="required"
              value="false"
              onClick={handleInputChange}
            />
            <label htmlFor="no">No</label>
          </div>
        </div>
        {['radio-btn', 'drop-down', 'check-box'].includes(
          tempQuestion.answerType
        ) &&
          tempQuestion.option.map((opt, index) => (
            <div key={index} className="wrap-option">
              <Option value={opt} index={index} setOptions={setOptions} />

              <button
                className="option-button close-btn"
                onClick={() => removeOption(index)}
              >
                <i className="fa-solid fa-xmark"></i>Remove
              </button>
            </div>
          ))}
        <button
          className="option-button add-btn"
          onClick={() => setOptions('option', 'new')}
        >
          <i className="fa-solid fa-plus"></i> Add Option
        </button>
      </div>
    </Component>
  );
}

const Component = styled.div`
  .question {
    box-shadow: 8px 8px 18px hsl(0 0% 0% / 0.14),
      -8px -8px 18px hsl(0 100% 100%);
    padding: 12px 7px;
    .question-div {
      background: red;
      position: relative;
      border-radius: 5px;
      margin-bottom: 1.5rem;
      height: 54px;
      .question-input {
        height: 100%;
        width: 100%;
        border: none;
        outline: none;
        border-radius: 5px;
        padding-top: 13px;
        padding-left: 8px;
        font: 400 1rem var(--openSansFont);
        &:focus + label,
        &:not(:placeholder-shown) + label {
          font-size: 0.87rem;
          top: 4px;
          color: hsl(0 0% 0% / 0.48);
        }
      }
      label {
        position: absolute;
        left: 8px;
        top: 16px;
        font: 400 1rem var(--openSansFont);
        transition: all 0.3s ease-in-out;
      }
    }
    .answer-type-div {
      /* background: linear-gradient(145deg, #dfdfdf, #f0f0f0); */
      /* box-shadow: 10px 10px 20px hsl(0 0% 0% /0.27), -10px -10px 20px #ffffff; */
      display: flex;
      flex-direction: column;
      gap: 8px;
      padding: 8px;
      border-radius: 5px;
      margin-bottom: 0.7rem;
      & > label {
        font: 400 1rem var(--openSansFont);
        color: hsl(0 0% 0% / 0.58);
      }
      .ans-type {
        display: grid;
        gap: 0.5rem;
        grid-template-columns: repeat(auto-fit, minmax(100px, 130px));
        .wrap-ans-type {
          display: flex;
          gap: 0.4rem;
          align-items: center;
          input {
            accent-color: hsl(265 100% 49%);
          }
          input:checked + label {
            color: hsl(265 100% 49%);
          }
          label {
            font: 400 1rem var(--openSansFont);
          }
        }
      }
    }
    .required-div {
      padding: 8px;
      display: flex;
      gap: 0.8rem;
      margin-bottom: 1rem;
      label {
        font: 400 1rem var(--openSansFont);
      }
      & > label {
        color: hsl(0 0% 0% / 0.58);
      }
      .wrap-required {
        display: flex;
        gap: 0.4rem;
        input {
          accent-color: hsl(265 100% 49%);
        }
        input:checked + label {
          color: hsl(265 100% 49%);
        }
      }
    }
    .wrap-option {
      display: flex;
      /* align-items: center; */
      gap: 1rem;
      position: relative;
      padding: 4px 0;
    }
    .option-button {
      /* align-self: center; */
      /* max-height: 100%; */
      gap: 0.4rem;
      background: transparent;
