import React, { useState, useEffect } from 'react';
import { Link, useNavigatem, useParams } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';
import { ToastContainer, toast } from 'react-toastify';

import { getSpecificSurvey } from '../utils/APIRoutes';

export default function TakeSurvey() {
  const { uid, id } = useParams();
  const [questions, setQuestions] = useState([]);
  const [survey, setsurvey] = useState({});
  useEffect(() => {
    if (id && uid) {
      async function getSurvey() {
        const { data } = await axios.post(`${getSpecificSurvey}/${id}/view`, {
          userId: uid,
        });
        data.survey.questions.map((q) => setQuestions(...questions, q));
        // setQuestions([...data.survey.questions]);
        setsurvey(data.survey);
        //   :id/view
      }
      getSurvey();
    }
  }, [id, uid]);
  // survey && questions.map((q) => console.log(q));
  return (
    <Component>
      <form action="">
        <h3>{survey.surveyName}</h3>
        
        {survey  &&
          questions.map((q) => {
            return <div key={q.id}>{q.question}</div>;
          })}
      </form>
    </Component>
  );
}

const Component = styled.div`
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
`;
