import React, { useState, useEffect } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import { v4 as uuidv4 } from 'uuid';

// import { Link, useNavigate } from 'react-router-dom';
// import axios from 'axios';
import styled from 'styled-components';

import CreateQuestion from '../components/CreateQuestion';

export const defaultQuestion = {
  questionId: '',
  question: '',
  answerType: '',
  required: '',
  option: ['option'],
};
export let randomQID = () => uuidv4();

export default function CreateForm() {
  const [formData, setFormData] = useState({
    surveyName: 'untitled',
    creatorId: '',
    createDate: new Date().toLocaleDateString(),
    amount: 0,
  });

  useEffect(() => {
    async function fetchAdminId() {
      const admin = await JSON.parse(localStorage.getItem('sur-uid'));
      admin && setFormData({ ...formData, creatorId: admin._id });
    }
    fetchAdminId();
  }, []);

  const [questions, setQuestions] = useState([]);
useEffect
  console.log('defq ', defaultQuestion);
  /*
  
  [{
  id:qid,
  question:'',
  answerType:'radio-btn,drop-down,check-box,text',
  required:boolean,
  option[]
  }]
  */
  function handleInputChange(e) {
    e.target.value.length < 32 &&
      setFormData({ ...formData, [e.target.name]: e.target.value });
  }

  function setQuestionFunction(index) {
    if (questions.length === 0) {
      !toast.isActive('sur-tid') &&
        toast.warning('duplicate question not allowed', {
          toastId: 'sur-tid',
          position: 'top-center',
          autoClose: 7000,
          pauseOnHover: true,
          draggable: true,
          theme: 'colored',
        });
      return;
    }
    console.log('clicked');
    setQuestions([...questions, { ...defaultQuestion }]);
  }

  return (
    <Component>
      <ToastContainer />

      <div className="create-form">
        <div className="header">
          <h2 className="title">Create Form</h2>
          <input
            type="text"
            name="surveyName"
            className="form-title"
            value={formData.surveyName}
            onChange={(e) => handleInputChange(e)}
          />
          <div className="wrap-amount">
            <label htmlFor="form-amount">Amount</label>
            <input
              id="form-amount"
              type="text"
              name="amount"
              className="form-amount"
              value={formData.amount}
              onChange={(e) => handleInputChange(e)}
            />
          </div>
        </div>
        <div className="main">
          {questions.length === 0 ? (
            <CreateQuestion
              questions={questions}
              data={questions}
              setQuestions={setQuestions}
            />
          ) : (
            questions.map((q, index) => {
              return (
                <CreateQuestion
                  key={index}
                  data={q}
                  questions={questions}
                  setQuestions={setQuestions}
                />
              );
            })
          )}
          <button
            className="add-question"
            onClick={() => setQuestionFunction('new')}
          >
            Add Question
          </button>
        </div>
      </div>
    </Component>
  );
}
const Component = styled.div`
  height: 100vh;
  .create-form {
    height: 100%;
    display: flex;
    flex-direction: column;
    .header {
      /* background: red; */
      padding: 2rem 1rem;
      display: flex;
      flex-direction: column;
      .title {
        font: 900 1.6rem var(--MontserratFont);
        background: linear-gradient(
          -39deg,
          hsl(260 100% 49%),
          hsl(265 100% 59%),
          hsl(267 100% 69%)
        );
        -webkit-text-fill-color: transparent;
        background-clip: text;
        -webkit-background-clip: text;
        align-self: center;
        margin-bottom: 2rem;
      }
      .form-title {
        height: 38px;
        width: 100%;
        border: none;
        outline: none;
        font: 500 1.3rem var(--openSansFont);
        background: none;
        padding-top: 10px;
        border-bottom: 1px solid hsl(265 100% 68%);
        color: hsl(265 100% 49%);
        margin-bottom: 2rem;
      }
      .wrap-amount {
        display: flex;
        align-items: center;
        gap: 1rem;
        font: 400 1.2rem var(--poppinsFont);
        label {
          color: hsl(0 0% 0% / 0.58);
        }
        .form-amount {
          width: 130px;
          font: inherit;
          border: 1px solid hsl(265 100% 69%);
          border-radius: 4px;
          padding: 2px 8px;
          outline: none;
          color: hsl(265 100% 49%);
          background: none;
        }
      }
    }
    .main {
      height: 100%;
      overflow-y: auto;
      padding: 2rem 1rem;
      margin-right: 4px;
      ::-webkit-scrollbar {
        width: 6px;
        background: transparent;
      }
      ::-webkit-scrollbar-thumb {
        background: linear-gradient(
          65deg,
          hsl(270 100% 49%),
          hsl(265 100% 39%),
          hsl(260 100% 49%)
        );
        border-radius: 50px;
      }
      /* background: red; */
      .add-question {
        padding: 8px;
        border: none;
        outline: none;
        font: 800 1rem var(--openSansFont);
        background: hsl(265 100% 45%);
        color: #cfcfcf;
        border-radius: 6px;
        margin: 1rem 0;
        cursor: pointer;
        &:hover,
        &:active {
          color: #ffffff;
        }
      }
    }
  }
`;
