import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import styled from 'styled-components';

export default function AllForms({ forms, setForms }) {
  // console.log('a', forms);
  return (
    <Container>
      {forms.map((form) => {
        // return <Form form={form} key={form._id} />;
        return (
          <div key={form._id} className="form">
            <h2>{form.surveyName} fasdasdfasdfsdasdf</h2>
            <div className="hr"></div>

            <div className="wrapper">
              <label>Number of questions</label>
              <div className="value">{form.questions.length}</div>
            </div>

            <div className="wrapper">
              <label>Amount allocated</label>
              <div className="value">{form.amount}</div>
            </div>

            <div className="wrapper">
              <label>date created</label>
              <div className="value">{form.createdDate}</div>
            </div>
            <div className="wrap-btn">
              <Link to={`/admin/home/${form._id}`}>View Response</Link>
              <button className="delete-form">Delete</button>
            </div>
          </div>
        );
      })}
    </Container>
  );
}

const Container = styled.div`
  height: 100%;
  display: grid;
  grid-template-columns: repeat(auto-fit, 240px);
  gap: 1.5rem;

  .form {
    height: 290px;
    border-radius: 5px;
    background: hsl(275 100% 50%);
    backdrop-filter: blur(18px);
    display: flex;
    flex-direction: column;
    padding: 0.89rem;
    h2 {
      font: 600 1.2rem var(--ralewayFont);
      color: #ffffff;
      white-space: nowrap;
      overflow: hidden;
      text-overflow: ellipsis;
    }
    .hr {
      height: 1px;
      width: 90%;
      margin: 0.8rem auto;
      background: hsl(0 100% 100% / 0.32);
    }
    .wrapper {
      display: flex;
      align-items: center;
      gap: 1rem;
      margin-bottom: 0.8rem;
      label {
        width: 50%;
        font: 400 0.91rem var(--openSansFont);
        color: hsl(0 100% 100% / 0.78);
      }
      .value {
        font: 400 0.91rem var(--poppinsFont);
        color: hsl(0 100% 100% / 0.88);
      }
    }
    .wrap-btn {
      margin-top: 0.8rem;
      display: flex;
      gap: 9px;
      button,
      a {
        padding: 8px;
        border: none;
        border-radius: 5px;
        outline: none;
        font: 700 1rem var(--openSansFont);
        text-align: center;
        box-shadow: 0 19px 29px hsl(0 0% 0% / 0.21);
      }
      .delete-form {
        background: hsl(8 100% 76%);
        color: hsl(0, 100%, 50%);
      }
      a {
        text-decoration: none;
        background: hsl(0 100% 100% / 0.55);
        color: hsl(275 100% 50%);
        &:hover,
        &:active {
          color: white;
        }
      }
    }
  }

  @media screen and (max-width: 608px) {
    justify-content: center;
  }
`;
