import React, { useState, useRef, useEffect } from 'react';
import { ToastContainer, toast } from 'react-toastify';

import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';

export default function CreateForm() {
  const [formData, setFormData] = useState({
    surveyName: 'untitled',
    creatorId: '',
    createDate: new Date().toLocaleDateString(),
    amount: 0,
  });

  useEffect(() => {
    async function fetchAdminId() {
      const admin = await JSON.parse(localStorage.getItem('sur-uid'));
      admin && setFormData({ ...formData, creatorId: admin._id });
    }
    fetchAdminId();
  }, []);

  formData.creatorId && console.log(formData);

  const [questions, setQuestions] = useState([]);
  /*
  
  [{
  id:qid,
  question:'',
  answerType:'radio-btn,drop-down,check-box,text',
  required:boolean,
  option[]
  }]
  */
  function handleInputChange(e) {
    setFormData({ ...formData, [e.target.name]: e.target.textContent });
  }

  return (
    <Component>
      <div className="create-form">
        <div className="header">
          {/* < className="form-title">{formData.surveyName}/> */}
          <input
            type="text"
            className="form-title"
            value={formData.surveyName}
          />
        </div>
        <div className="main"></div>
      </div>
    </Component>
  );
}
const Component = styled.div`
  height: 100vh;
  padding: 5% 7%;
  .create-form {
    height: 100%;
    display: flex;
    flex-direction: column;
  }
`;
