      }
    }
    .create-btn {
      margin-left: auto;
      border: none;
      outline: none;
      color: #ececec;
      background: hsl(190 88% 48%);
      border-radius: 4px;
      padding: 0.4rem 0.8rem;
      font: 600 1.4rem var(--openSansFont);
      cursor: pointer;
    }
  }
  @media screen and (max-width: 768px) {
    header {
      .greeting {
        h3 {
          font-size: 1.13rem;
        }
      }
      .create-btn {
        font-size: 0.95rem;
      }
    }
  }
`;
