/* eslint-disable react-hooks/exhaustive-deps */
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';
import { ToastContainer, toast } from 'react-toastify';

import { getSpecificSurvey, submitSurvey } from '../utils/APIRoutes';

export default function TakeSurvey() {
  const navigate = useNavigate();
  const { uid, id, cid } = useParams();
  const answerRef = useRef([]);
  const [answers, setAnswers] = useState([]);
  const [survey, setsurvey] = useState({
    surveyName: '',
    questions: [],
    amount: 0,
  });
  useEffect(() => {
    if (id && uid) {
      async function getSurvey() {
        const { data } = await axios.post(`${getSpecificSurvey}/${id}/view`, {
          userId: uid,
        });
        if (data.status === 'completed') {
          !toast.isActive('sur-tid') &&
            toast.success(data.msg, {
              toastId: 'sur-tid',
              position: 'top-center',
              autoClose: 5000,
              closeButton: false,
              pauseOnHover: true,
              delay: 0,
              draggable: true,
              theme: 'colored',
            });
          setTimeout(() => navigate(-1), 6000);
        }
        if (data.status === true) {
          setsurvey({
            surveyName: data.survey.surveyName,
            questions: data.survey.questions,
            amount: data.survey.amount,
          });
          setAnswers([]);
          data.survey.questions.forEach((data) => {
            setAnswers((prev) => [...prev, { ...data, option: [] }]);
          });
        }
      }
      getSurvey();
    }
  }, [id, uid]);
  // {
  // 	"surveyId":"6352b9011339d12313798463",
  // 	"userId":"63535cf972c40610b643c1e3",
  // 	"answers": [{"id":"100","question":"choose elective 1","answerType":"radio-btn","required":true,"option":["ooad"]},
  // 							{"id":"101","question":"choose elective 2","answerType":"drop-down","required":true,"option":["ui/ux"]},
  // 							{"id":"103","question":"choose elective 3","answerType":"radio-btn","required":true,"option":["block chain"]}]
  //   creatorId,
  //   surveyName
  // }

  async function sendSurvey(e) {
    console.log(answers);
    const { data } = await axios.post(submitSurvey, {
      surveyId: id,
      userId: uid,
      answers: answers,
      creatorId: cid,
      surveyName: survey.surveyName,
    });
    if (data.status) {
      let users = await JSON.parse(localStorage.getItem('sur-uid'));
      users.amount += survey.amount;
      users.surveys.push(id);
      localStorage.setItem('sur-uid', JSON.stringify(users));
      !toast.isActive('sur-tid') &&
        toast.success(data.msg, {
          toastId: 'sur-tid',
          position: 'top-center',
          autoClose: 5000,
          pauseOnHover: true,
          draggable: true,
          theme: 'colored',
        });
      setTimeout(() => navigate(-1), 5600);
    }
  }

  function handleAnswerChange(qid, name, value) {
    let tempAnswers = [...answers];
    console.log(qid, name, value);
    if (name === 'radio-btn' || name === 'drop-down' || name === 'text') {
      tempAnswers.map(
        (ans) => ans.questionId === qid && (ans.option = [value])
      );
      setAnswers([...tempAnswers]);
    }
    if (name === 'check-box') {
      tempAnswers.forEach((ans) => {
        if (ans.questionId === qid) {
          [...ans.option].includes(value)
            ? (ans.option = [...ans.option].filter((op) => op !== value))
            : (ans.option = [...ans.option, value]);
        }
      });
      setAnswers([...tempAnswers]);
    }
  }

  return (
    <Component>
      <ToastContainer />
      {survey.questions.length > 0 && (
        <form onSubmit={sendSurvey}>
          <h3>{survey && survey.surveyName}</h3>
          {survey.questions &&
            survey.questions.map((q) => {
              return (
                <div className="set" id={q.questionId} key={q.questionId}>
                  <div className="question">{q.question}</div>
                  {q.answerType === 'radio-btn' &&
                    q?.option.map((opt, index) => {
                      return (
                        <div
                          key={`${q.questionId}-${index}-radio`}
                          className="radio"
                        >
                          <input
                            name={q?.questionId}
                            id={`${q.questionId}-${index}`}
                            type="radio"
                            onClick={() => {
                              handleAnswerChange(
                                q.questionId,
                                'radio-btn',
                                opt
                              );
                            }}
                          />
                          <label htmlFor={`${q.questionId}-${index}`}>
                            {opt}
                          </label>
                        </div>
                      );
                    })}
                  {q.answerType === 'drop-down' && (
                    <select
                      onClick={(e) => {
                        handleAnswerChange(
                          q.questionId,
                          'drop-down',
                          e.target.value
                        );
                      }}
                      name={q.questionId}
                      id={q.questionId}
                    >
                      {q.option.map((opt, index) => {
                        return (
                          <option key={`${q.questionId}-${index}-drop-down`}>
                            {opt}
                          </option>
                        );
                      })}
                    </select>
                  )}
                  {q.answerType === 'text' && (
                    <input
                      type="text"
                      onChange={(e) => {
                        handleAnswerChange(
                          q.questionId,
                          'text',
                          e.target.value
                        );
                      }}
                      className="text"
                      name={q.id}
                    />
                  )}
                  {q.answerType === 'check-box' &&
                    q.option.map((opt, index) => {
                      return (
                        <div
                          key={`${q.questionId}-${index}-check-box`}
                          className="check-box"
                        >
                          <input
                            onClick={() => {
                              handleAnswerChange(
                                q.questionId,
                                'check-box',
                                opt
                              );
                            }}
                            name={`${q?.questionId}-${index}`}
                            id={`${q.questionId}-${index}`}
                            type="checkbox"
                            value={opt}
                          />
                          <label htmlFor={`${q.questionId}-${index}`}>
                            {opt}
                          </label>
                        </div>
                      );
                    })}
                </div>
              );
            })}
          <button className="submit">Submit</button>
        </form>
      )}
    </Component>
  );
}

const Component = styled.div`
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background: #ffffff99;
  form {
    width: 76%;
    height: 100%;
    display: flex;
    flex-direction: column;
    padding: 1% 0.8rem;
    /* background: hsl(0 100% 100% / 0.35); */
    /* background: linear-gradient(145deg, hsl(270 100% 39%), hsl(280 100% 35%)); */
    /* backdrop-filter: blur(29px) saturate(100%); */
    /* box-shadow: 0 0 30px hsl(0 0% 0% / 0.21); */
    /* overflow-y: auto; */
    ::-webkit-scrollbar {
      width: 7px;
      background: transparent;
    }
    ::-webkit-scrollbar-thumb {
      background: hsl(0 100% 100% / 0.52);
      backdrop-filter: blur(28px);
      -webkit-backdrop-filter: blur(28px);
      border-radius: 50px;
    }
    h3 {
      font: 500 1.2rem var(--montserratFont);
      margin-bottom: 1rem;
      align-self: center;
      color: #ffffff;
    }
    .set {
      margin-bottom: 1.4rem;
    }
    .question {
      font: 400 1.1rem var(--poppinsFont);
      margin-bottom: 0.8rem;
      color: #ffffffda;
    }
    .radio,
    .check-box {
      display: flex;
      /* flex-direction: column; */
      margin-bottom: 0.3rem;
      gap: 0.3rem;
      input {
        border: none;
        font-size: 0.9rem;
        background: none;
        accent-color: #282828;
      }
      label {
        font: 400 0.91rem var(--poppinsFont);
        color: #ffffff99;
      }
    }
    .text {
      border: none;
      outline: none;
      padding: 0.5rem;
      font: 400 0.94rem var(--openSansFont);
      color: #f8f8f8;
      width: 100%;
      background: hsl(0 100% 100% / 0.12);
      backdrop-filter: blur(28px);
      -webkit-backdrop-filter: blur(28px);
      border-radius: 5px;
      box-shadow: 0 0 30px hsl(0 0% 0% / 0.31);
    }
    select {
      border: none;
      outline: none;
      cursor: pointer;
      padding: 0.4rem 0.7rem;
      box-shadow: 0 9px 28px #0000004a;
      font: 400 0.99rem var(--openSansFont);
      border-radius: 5px;
    }
    .submit {
      align-self: center;
      padding: 0.4rem;
      font: 800 1.4rem var(--openSansFont);
      width: 50%;
      border-radius: 5px;
      border: none;
      outline: none;
      color: hsl(270 100% 59%);
      cursor: pointer;
      box-shadow: 0 9px 28px hsl(0 0% 0%/ 0.29);
    }
  }


  @media screen and (max-width:768px){
    form{
      padding: 0 1rem;
      width: 100%;
    }
  }
`;
