import React, { useState, useEffect } from 'react';
import { Link, useNavigatem, useParams } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';
import { ToastContainer, toast } from 'react-toastify';

import { getSpecificSurvey } from '../utils/APIRoutes';

export default function TakeSurvey() {
  const { uid, id } = useParams();
  const [survey, setsurvey] = useState({
    surveyName: '',
    questions: [],
    amount: 0,
  });
  useEffect(() => {
    if (id && uid) {
      async function getSurvey() {
        const { data } = await axios.post(`${getSpecificSurvey}/${id}/view`, {
          userId: uid,
        });
        setsurvey({
          surveyName: data.survey.surveyName,
          questions: [...survey.questions, data.survey.questions],
          amount: data.survey.amount,
        });
      }
      getSurvey();
    }
  }, [id, uid]);
  console.log(survey.questions.map((e) => console.log(e)));
  return (
    <Component>
      <form action="">
        <h3>{survey.surveyName}</h3>
        {survey.questions.length > 0 &&
          survey.questions.map((question) => {
            return <div key={question.id}>{question.id}</div>;
          })}
      </form>
    </Component>
  );
}

const Component = styled.div`
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 2% 1%;
  form {
    width: 380px;
    height: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 1%;
    background: red;
    h3 {
      font: 500 1.2rem var(--ralewayFont);
      margin-bottom: 1rem;
    }
    .question {
      font: 400 1.1rem var(--poppinsFont);
      align-self: flex-start;
      margin-right: auto;
      background: lime;
      margin-bottom: 0.8rem;
    }
  }
`;
