/* eslint-disable react-hooks/exhaustive-deps */
import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';
import { ToastContainer, toast } from 'react-toastify';

import { getAllSurvey } from '../utils/APIRoutes';

export default function UserHome() {
  const navigate = useNavigate();
  //   const [user, setUser] = useState({ id: '', name: '', survey: [], amount: 0 });
  const [user, setUser] = useState({});
  const [survey, setSurvey] = useState({
    status: false,
    data: [],
  });

  useEffect(() => {
    async function fetchUser() {
      if (!localStorage.getItem('sur-uid')) navigate('/user/login');
      else {
        setUser(JSON.parse(localStorage.getItem('sur-uid')));
      }
    }
    fetchUser();
  }, []);
  useEffect(() => {
    async function fetchForms() {
      if (user) {
        const { data } = await axios.post(getAllSurvey);
        if (data.status === false)
          !toast.isActive('sur-tid') &&
            toast.error(data.msg, {
              toastId: 'sur-tid',
              position: 'top-right',
              autoClose: 7000,
              pauseOnHover: true,
              draggable: true,
              theme: 'dark',
            });
        if (data.status === true)
          setSurvey({ status: data.status, data: data.response });
      }
    }
    fetchForms();
  }, []);
  return (
    <Component>
      <ToastContainer />

      <header>
        <div className="greeting">
          <h3>
            Welcome <span>{user.name}</span> ðŸ‘‹
          </h3>
        </div>
        <div className="wallet">amount: {user.amount}</div>
      </header>
      <div className="wrap-form">
        {survey.status &&
          survey.data.map((data) => {
            return (
              <div className="card" key={data._id}>
                <h2>{data.surveyName}</h2>
                <div className="amount">
                  amount: <span>{data.amount}</span>
                </div>
                <div className="data">Created Date: {data.createdDate}</div>
                {user?.id && user?.survey.includes(data._id) ? (
                  <Link>Completed</Link>
                ) : (
                  <Link
                    to={`/user/${user._id}/take-survey/${data._id}/${data.creatorId}`}
                  >
                    Fill Form
                  </Link>
                )}
              </div>
            );
          })}
      </div>
    </Component>
  );
}
const Component = styled.div`
  height: 100vh;
  padding: 3% 4% 1%;
  background: #dfdfdf;
  display: flex;
  flex-direction: column;
  header {
    display: flex;
    align-items: center;
    margin-bottom: 1rem;
    /* border-radius: 3px;
    background: hsl(270 88% 48% / 0.15);
    box-shadow: 0 8px 12px hsl(0 0% 0% / 0.09);
    backdrop-filter: blur(28px) saturate(100%); */

    .greeting {
      h3 {
        font: 500 1.3rem var(--openSansFont);
      }
    }
    .wallet {
      margin-left: auto;
      font: 500 1.3rem var(--openSansFont);
    }
  }
  .wrap-form {
    display: grid;
    grid-template-columns: repeat(auto-fit, 280px);
    align-items: flex-start;
    height: 100%;
    overflow-y: auto;
    padding: 5%;
    ::-webkit-scrollbar {
      width: 6px;
      background: transparent;
    }
    ::-webkit-scrollbar-track {
      background: #272727;
    }

    .card {
      display: flex;
      flex-direction: column;
      gap: 0.8rem;
      padding: 5% 4%;
      border-radius: 7px;
      box-shadow: -18px -18px 28px hsl(0 100% 100%),
        18px 18px 28px hsl(0 0% 0%);
      background: hsl(0 0% 0% / 0.78);
    }
  }
`;
