import axios from 'axios';
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import styled from 'styled-components';

import { fetchParticularForms } from '../utils/APIRoutes.js';
export default function Form() {
  const [form, setForm] = useState([]);
  const [report, setReport] = useState({
    name: '',
    data: [],
  });
  const params = useParams();

  useEffect(() => {
    async function fetchForm() {
      if (params.id !== '') {
        const { data } = await axios.get(
          `${fetchParticularForms}/${params.id}`
        );
        if (data.status) setForm(data.response);
        if (data.status)
          setReport({
            name: data.report.surveyName,
            data: data.report.questions,
          });
      }
    }
    fetchForm();
  }, [params.id]);
  // form.length > 0 && console.log('a', form);
  // form.length > 0 && console.log('r', report);
  // answers.length > 0 && console.log(answers);
  // answers.length > 0 && console.log(questions);

  report.data.lenght > 0 &&
    report.data.forEach((r) => {
      <div className="question">{r.question}</div>;
    });

  return (
    <Container>
      <div className="form">
        <div className="header">
          <h2>{report.name}</h2>
          <h4>
            Total Responses <span>{form?.length}</span>
          </h4>
        </div>
        <div className="hr"></div>
        {report.data.lenght > 0 &&
          report.data.forEach((r) => {
            <div className="question">{r.question}</div>;
          })}
      </div>
    </Container>
  );
}

const Container = styled.div`
  height: 100vh;
  padding: 3% 2%;
  display: flex;
  justify-content: center;
  .form {
    height: 100%;
    width: 500px;
    backdrop-filter: blur(28px);
    border-radius: 3px;
    box-shadow: -18px -18px 39px hsl(0 100% 100%),
      18px 18px 39px hsl(0 0% 0% / 0.11);
    .header {
      margin-top: 1rem;
      display: flex;
      flex-direction: column;
      align-items: center;
      gap: 0.5rem;
      h2 {
        font: 550 1.4rem var(--ralewayFont);
        color: #292929;
      }
      h4 {
        font: 400 1rem var(--openSansFont);
        color: #393939;
      }
    }
    .hr {
      height: 1px;
      width: 90%;
      margin: 0.8rem auto;
      background: hsl(0 0% 0% / 0.22);
    }
  }
`;
