import React, { useState, useRef, useEffect } from 'react';
import { ToastContainer, toast } from 'react-toastify';

import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';

export default function CreateForm() {
  const [formData, setFormData] = useState({
    surveyName: 'untitled',
    creatorId: '',
    createDate: new Date().toLocaleDateString(),
    amount: 0,
  });

  useEffect(() => {
    async function fetchAdminId() {
      const admin = await JSON.parse(localStorage.getItem('sur-uid'));
      admin && setFormData({ ...formData, creatorId: admin._id });
    }
    fetchAdminId();
  }, []);

  const [questions, setQuestions] = useState([]);
  /*
  
  [{
  id:qid,
  question:'',
  answerType:'radio-btn,drop-down,check-box,text',
  required:boolean,
  option[]
  }]
  */
  function handleInputChange(e) {
    e.target.value.length < 32 &&
      setFormData({ ...formData, [e.target.name]: e.target.value });
  }

  return (
    <Component>
      <div className="create-form">
        <div className="header">
          <h2 className='title'>Create Form</h2>
          <input
            type="text"
            name="surveyName"
            className="form-title"
            value={formData.surveyName}
            onChange={(e) => handleInputChange(e)}
          />
          <div className="wrap-amount">
            <label htmlFor="form-amount">Amount</label>
            <input
              id="form-amount"
              type="text"
              name="amount"
              className="form-amount"
              value={formData.amount}
              onChange={(e) => handleInputChange(e)}
            />
          </div>
        </div>
        <div className="main"></div>
      </div>
    </Component>
  );
}
const Component = styled.div`
  height: 100vh;
  .create-form {
    height: 100%;
    display: flex;
    flex-direction: column;
    .header {
      /* background: red; */
      box-shadow: 0 0 18px #00000034;
      padding: 2rem 1rem;
      .title{
        
      } .form-title {
        height: 38px;
        width: 100%;
        border: none;
        outline: none;
        font: 500 1.3rem var(--openSansFont);
        background: none;
        padding-top: 12px;
        border-bottom: 1px solid hsl(265 100% 68%);
        /* color: hsl(280 100% 55%); */
        margin-bottom: 0.5rem;
      }
    }
  }
`;
