import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import styled from 'styled-components';

export default function Forms({ form }) {
  return (
    <Component>
      <h2>{form.surveyName} fasdasdfasdfsdasdf</h2>
      <div className="hr"></div>

      <div className="wrapper">
        <label>Number of questions</label>
        <div className="value">{form.questions.length}</div>
      </div>

      <div className="wrapper">
        <label>Amount allocated</label>
        <div className="value">{form.amount}</div>
      </div>

      <div className="wrapper">
        <label>date created</label>
        <div className="value">{form.createdDate}</div>
      </div>
      {/* <button className="view-btn"> */}
        <Link>View Form</Link>
      {/* </button> */}
    </Component>
  );
}

const Component = styled.div`
  height: 290px;
  border-radius: 5px;
  background: hsl(275 100% 50%);
  backdrop-filter: blur(18px);
  box-shadow: 0 18px 29px #00000048;
  display: flex;
  flex-direction: column;
  padding: 0.89rem;
  h2 {
    font: 600 1.2rem var(--ralewayFont);
    color: #ffffff;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
  }
  .hr {
    height: 1px;
    width: 90%;
    margin: 0.8rem auto;
    background: hsl(0 100% 100% / 0.32);
  }
  .wrapper {
    display: flex;
    align-items: center;
    gap: 1rem;
    margin-bottom: 0.8rem;
    label {
      width: 50%;
      font: 400 0.91rem var(--openSansFont);
      color: hsl(0 100% 100% / 0.78);
    }
    .value {
      font: 400 0.91rem var(--poppinsFont);
      color: hsl(0 100% 100% / 0.88);
    }
  }
  a {
    margin-top: 0.8rem;
    border: none;
    outline: none;
    background: hsl(0 100% 100% / 0.55);
    padding: 0.5rem;
    border-radius: 4px;
    box-shadow: 0 19px 29px hsl(0 0% 0% / 0.21);
    color: hsl(275 100% 50%);
    &:hover,
    &:active {
      color: white;
    }
      font: 700 1rem var(--ralewayFont);
      text-decoration: none;
    }
  }
`;
