import React from 'react';

import styled from 'styled-components';

import { v4 as uuidv4 } from 'uuid';

export default function CreateQuestion() {
  return (
    <Component>
      <div id={uuidv4()} className="question">
        <div className="question-div">
          <input type="text" className="question-input" placeholder=' '/>
          <label htmlFor="q-input">question</label>
        </div>
      </div>
    </Component>
  );
}

const Component = styled.div`
  .question {
    box-shadow: 8px 8px 18px hsl(0 0% 0% / 0.14),
      -8px -8px 18px hsl(0 100% 100%);
    padding: 12px 7px;
    .question-div {
      background: red;
      position: relative;
      border-radius: 5px;

      height: 54px;
      .question-input {
        height: 100%;
        width: 100%;
        border: none;
        outline: none;
        border-radius: 5px;
        padding-top: 13px;
        padding-left: 8px;
        font: 400 1rem var(--openSansFont);
        &::focus
    }
    label {
        position: absolute;
        left: 8px;
        top: 16px;
        font: 400 1rem var(--openSansFont);
      }
    }
  }
`;
