import axios from 'axios';
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import styled from 'styled-components';
import { fetchParticularForms } from '../utils/APIRoutes.js';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  ArcElement,
  Tooltip,
  Legend,
} from 'chart.js';

import { Bar, Pie } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  ArcElement,
  Tooltip,
  Legend
);

export const barOptions = {
  responsive: true,
  plugins: {
    legend: {
      position: 'top',
    },
    title: {
      display: false,
      // text: 'Chart.js Bar Chart',
    },
    maintainAspectRatio: false,
  },
};

export default function Form() {
  const [form, setForm] = useState([]);
  const [report, setReport] = useState({
    name: '',
    data: [],
  });
  const params = useParams();

  useEffect(() => {
    async function fetchForm() {
      if (params.id !== '') {
        const { data } = await axios.get(
          `${fetchParticularForms}/${params.id}`
        );
        if (data.status) setForm(data.response);
        if(data.status && data.res)
        if (data.status)
          setReport({
            name: data.report.surveyName,
            data: data.report.questions,
          });
      }
    }
    fetchForm();
  }, [params.id]);
  // form.length > 0 && console.log('a', form);
  // form.length > 0 && console.log('r', report);
  // answers.length > 0 && console.log(answers);
  // answers.length > 0 && console.log(questions);
  // report.data.length > 0 && console.log(report.data);

  return (
    <Container>
      <div className="form">
        <div className="header">
          <h2>{report.name}</h2>
          <h4>
            Total Responses <span>{form?.length}</span>
          </h4>
        </div>
        <div className="hr"></div>
        <div className="report">
          {report.data.length > 0 &&
            report.data.map((r) => {
              return (
                <div className="wrap" key={r.id}>
                  <div className="question">{r.question}</div>
                  <div className="wrap-chart">
                    {r.answerType === 'radio-btn' && (
                      <Pie
                        datasetIdKey={r.id}
                        data={{
                          labels: [...r.option],
                          datasets: [
                            {
                              label: '#no of votes',
                              data: [...r.answers],
                              backgroundColor: [...r.customBackground],
                              borderColor: [...r.customBorder],
                              borderWidth: 1,
                            },
                          ],
                        }}
                        options={{ maintainAspectRatio: false }}
                      />
                    )}
                    {(r.answerType === 'check-box' ||
                      r.answerType === 'drop-down') && (
                      <Bar
                        datasetIdKey={r.id}
                        data={{
                          labels: [...r.option],
                          datasets: [
                            {
                              label: `${r.question}`,
                              data: [...r.answers],
                              backgroundColor: [...r.customBackground],
                              borderColor: [...r.customBorder],
                              borderWidth: 1,
                            },
                          ],
                        }}
                        options={barOptions}
                      />
                    )}
                  </div>
                </div>
              );
            })}
        </div>
      </div>
    </Container>
  );
}

const Container = styled.div`
  height: 100vh;
  padding: 3% 2%;
  display: flex;
  justify-content: center;
  .form {
    height: 100%;
    width: 500px;
    display: flex;
    flex-direction: column;
    backdrop-filter: blur(28px);
    border-radius: 3px;
    box-shadow: -18px -18px 39px hsl(0 100% 100%),
      18px 18px 39px hsl(0 0% 0% / 0.11);
    .header {
      margin-top: 1rem;
      display: flex;
      flex-direction: column;
      align-items: center;
      gap: 0.5rem;
      h2 {
        font: 550 1.4rem var(--ralewayFont);
        color: #292929;
      }
      h4 {
        font: 400 1rem var(--openSansFont);
        color: #393939;
      }
    }
    .hr {
      height: 1px;
      width: 90%;
      margin: 0.8rem auto;
      background: hsl(0 0% 0% / 0.22);
    }
    .report {
      padding: 8px;
      height: 100%;
      overflow-y: auto;
      display: flex;
      flex-direction: column;
      gap: 1rem;
      margin-right: 4px;
      ::-webkit-scrollbar {
        width: 6px;
        padding: 9px;
        background: transparent;
      }
      ::-webkit-scrollbar-thumb {
        background: linear-gradient(
          143deg,
          hsl(270 100% 58%),
          hsl(265 100% 48%),
          hsl(260 100% 58%)
        );
        border-radius: 50px;
      }
      .wrap {
        display: flex;
        flex-direction: column;
      }
      .question {
        font: 500 1rem var(--openSansFont);
        margin-bottom: 1rem;
      }
      .wrap-chart {
        width: 90%;
        height: 300px;
        align-self: center;
      }
    }
  }
`;
